import { useEffect, useState } from "react"
import { retrieveAllToDos, retrieveTodoForId } from "./api/TodoApiservice"

function ToDoListComponent()
{

    const [ToDos, setToDos] = useState([])
    function refreshToDos()
    {
        retrieveAllToDos('in28Minutes')
        .then( (response) => { setToDos(response.data) } )
        .catch( (error) => console.log(error))
    }
    function getToDo()
    {
        retrieveTodoForId('in28Minutes',1)
        .then( response => console.log(response.data))
        .catch( error => console.log(error))

    }
    function dleteToDo(id)
    {
        console.log(id)
    }

    useEffect( () => { refreshToDos()}, [] )
    return(
        <div className="container">
            <h1>Things You need to do</h1>
            <div>
                <table className="table">
                    <thead>
                        <tr>
                            <th>Description</th>
                            <th>Status</th>
                            <th>Target Date</th>
                            
                        </tr>
                    </thead>
                    <tbody>
                        {
                            ToDos.map(
                                todo=>
                                  <tr key={todo.id}>
                                    <td>{todo.description}</td>
                                    <td>{todo.done.toString()}</td>
                                    <td>{todo.targetDate.toString()}</td>
                                    <td><button className="btn btn-danger" onClick={() => dleteToDo(todo.id)}>Delete</button></td>
                                  </tr>
                            )
                        }
                    </tbody>
                </table>
            </div>
            <button className="btn btn-success" onClick={getToDo}>get todo</button>
        </div>
    )
}
export default ToDoListComponent
