import { useState } from "react"
import { useParams, Link} from "react-router-dom"

import { helloWorldReq, helloWorlBeandReq } from "./api/HelloWorldApiService"
function Welcome()
{
    const param = useParams()
    const [message, setMessage] = useState(null)
    function callHelloWorldResyApi()
    {
        console.log('hello')
        helloWorldReq()
        .then((response)=> successfullRespnse(response))
        .catch((error)=> failureResponse(error))
        .finally(()=>console.log('clean up'))

        // helloWorlBeandReq()
        // .then((response)=> successfullRespnse(response))
        // .catch((error)=> failureResponse(error))
        // .finally(()=>console.log('clean up'))
    }

    function successfullRespnse(response)
    {
        setMessage(response.data)
        console.log(response)
    }

    function failureResponse(error)
    {
        console.log(error)
    }

    return(
        <div className="welcomeComponent">
            <h1>Welcome {param.username}</h1>
            <div>
                <h3>How are you Today</h3>
                Manage Your ToDos- <Link to="/todolist">click here </Link>
                <div><button className="btn btn-success m-5" onClick={callHelloWorldResyApi}>call Hello World</button></div>
                <div className="text-info">{message}</div>
            </div>
        </div>
    )
}
export default Welcome
