import { useState } from "react"
import { BrowserRouter, Routes, Route, Navigate} from "react-router-dom"
import './ToDo.css';

import LogoutComponent from "./LogoutComponent";
import FooterComponent from "./FooterComponent";
import HeaderComponent from "./HeaderComponent";
import ToDoListComponent from "./ToDoListComponent";
import ErrorComponent from "./ErrorComponent";
import Welcome from "./WelcomeComponent";
import LogIn from "./LogInComponent";

import AuthContext, { useAuth } from "./security/AuthContext";

function AuthenticatdRoute({children})
{
    const authContext = useAuth()
    if(authContext.isAuthenticated) 
        return children
    return <Navigate to='/login'/>
}

export default function ToDoApp()
{
    return(
        <div className="ToDoApp">
            <AuthContext>
                <BrowserRouter>
                    <HeaderComponent/>
                    <Routes>
                    <Route path="/" element={<LogIn/>}/>
                        <Route path="/login" element={<LogIn/>}/>
                        <Route path="/welcome/:username" element={<AuthenticatdRoute><Welcome/></AuthenticatdRoute>}/>
                        <Route path="/todolist" element={<AuthenticatdRoute><ToDoListComponent/></AuthenticatdRoute>}/>
                        <Route path="/logout" element={<AuthenticatdRoute><LogoutComponent/></AuthenticatdRoute>}/>
                        <Route path="*" element={<ErrorComponent/>}/>
                    </Routes>
                    <FooterComponent/>
                </BrowserRouter>
            </AuthContext>
        </div>
    )
}
