import { useState } from "react"
import { BrowserRouter, Routes, Route, useNavigate} from "react-router-dom"

export default function ToDoApp()
{
    return(
        <div className="ToDoApp">
            <h1>ToDo Application</h1>
            <BrowserRouter>
                <Routes>
                <Route path="/" element={<LogIn/>}></Route>
                    <Route path="/login" element={<LogIn/>}></Route>
                    <Route path="/welcome" element={<Welcome/>}></Route>
                    <Route path="*" element={<ErrorComponent/>}></Route>
                </Routes>
            </BrowserRouter>
            
        </div>
    )
}

function LogIn()
{
    const [username, setusername] = useState('Nanda')
    const [password, setPassword] = useState('')
    const [showSuccess, setShowSuccess] = useState(false)
    const [showFailure, setShowFsilure] = useState(false)
    const navigate = useNavigate()
    function handleUsernameChange(event)
    {
        setusername(event.target.value)
    }

    function handlePasswordChange(event)
    {
        setPassword(event.target.value)
    }

    function LogInValidation()
    {
        if(username==='Nanda' && password==='WASDskal')
        {
            setShowSuccess(true)
            setShowFsilure(false)
            navigate('/welcome')
        }
        else
        {
            setShowSuccess(false)
            setShowFsilure(true)
        }
    }
    return(
        <div className="LogIn">
            <div className="LogInform">
                <div>
                    {showSuccess && <div>LogIn Successfull</div>}
                    {showFailure && <div>LogIn Failed try again</div>}
                    <label>UserName</label>
                    <input type="text" name="username" value={username} onChange={handleUsernameChange}/>
                </div>
                <div>
                    <label>Password</label>
                    <input type="password" name="passeord" value={password} onChange={handlePasswordChange}/>
                </div>
                <button name="LogIn" onClick={LogInValidation}>LogIn</button>
            </div>
        </div>
    )
}

function Welcome()
{
    return(
        <div>Welcome Component</div>
    )
}

function ErrorComponent()
{
    return(
        <div className="errorComponent">
            <h1>We are working really hard</h1>
            <div>
                <h3>Apologies for 404. Reach out for our team XXXXXXX</h3>
            </div>
        </div>
    )
}
