import { useState } from 'react'
import './Counter.css'

export default function Counter()
{
    let [currentCount, setCount] = useState(0)
    function TotalIncrement(by)
    {
        setCount(currentCount + by)
    }

    function TotalDecrement(by)
    {
        if(currentCount<=1) currentCount=0
        setCount(currentCount - by)
    }
    return(
        <>
            <span className="count">{currentCount}</span>
            <Count by={1} TotalIncrement={TotalIncrement} TotalDecrement={TotalDecrement}/>
            <Count by={3} TotalIncrement={TotalIncrement} TotalDecrement={TotalDecrement}/>
            <Count by={5} TotalIncrement={TotalIncrement} TotalDecrement={TotalDecrement}/>
        </>
    )
}

function Count({by, TotalIncrement, TotalDecrement})
{

    function increment1()
    {
        TotalIncrement(by)
    }

    function decrement1()
    {
        TotalDecrement(by)
    }
    return(<div className="counter">
                <div>
                    <button className="decrement" onClick={decrement1}>-{by}</button>
                    <button className="increment" onClick={increment1}>+{by}</button>
                </div>
          </div>
    )

}
