import { useState } from 'react'
import './Counter.css'
export default function Counter()
{

    let [currentCount, setCount] = useState(0)
    function increment1({by})
    {
        setCount(currentCount+by)
    }

    function decrement1({by})
    {
        if(currentCount<=1) currentCount=1
        setCount(currentCount-by)
    }
    return(<div className="counter">
                <span className="count">{currentCount}</span>
                <div>
                    <button className="increment" onClick={increment1}>{by}</button>
                    <button className="decrement" onClick={decrement1}>{by}</button>
                </div>
          </div>
    )

}
