import { useEffect, useState } from 'react'
import { retrieveTodoForIdApi } from './api/TodoApiservice'
import { useAuth } from './security/AuthContext'
import { useParams } from 'react-router-dom'

function ToDoCompoent()
 {
    const {id} = useParams()
    const AuthContext = useAuth()
    const [todo, settodo] = useState()
    function getToDo()
    {
        retrieveTodoForIdApi(AuthContext.username,id)
        .then( 
                response => 
                    { 
                        console.log(response.data)
                        settodo(response.data)
                    }
             )
        .catch( error => console.log(error))

    }
    console.log(id)
    // useEffect(getToDo(id), [id])
    return(
        <div className="container">
            <h1>To Do details</h1>
            <div>
                {/* <h3>Description : ${todo.description}</h3>1 */}
                <button onClick={getToDo}></button>
            </div>
        </div>
    )
 }
 export default ToDoCompoent
