import { useState } from 'react'
import './Counter.css'
export default function Counter()
{

    let [currentCount, setCount] = useState(0)
    function increment1()
    {
        setCount(currentCount+1)
        console.log(currentCount)
        console.log("incrementd plus 1")
    }

    function decrement1()
    {
        if(currentCount<=1)
            currentCount=1
        setCount(currentCount-1)
    }
    return(<div className="counter">
                <span className="count">{currentCount}</span>
                <div>
                    <button className="increment" onClick={increment1}>+1</button>
                    <button className="decrement" onClick={decrement1}>-1</button>
                </div>
          </div>
    )

}
