import { useEffect, useState } from "react"
import { retrieveAllToDosApi, deleteToDoApi } from "./api/TodoApiservice"

function ToDoListComponent()
{

    const [ToDos, setToDos] = useState([])
    function refreshToDos()
    {
        retrieveAllToDosApi('in28Minutes')
        .then( (response) => { setToDos(response.data) } )
        .catch( (error) => console.log(error))
    }
    // function getToDo()
    // {
    //     retrieveTodoForIdApi('in28Minutes',1)
    //     .then( response => console.log(response.data))
    //     .catch( error => console.log(error))

    // }
    const [message, setMessage] = useState(null)
    function dleteToDo(id)
    {
        deleteToDoApi('in28Minutes',id)
        .then(
                () => {
                        setMessage(`Deleted a To Do with id ${id}`)
                        refreshToDos()
                      }
            )
        .catch( error => console.log(error))
    }

    useEffect( () => { refreshToDos()}, [] )
    return(
        <div className="container">
            <h1>Things You need to do</h1>
            <div>
                <div className="alert alert-warning">{message}</div>
                <table className="table">
                    <thead>
                        <tr>
                            <th>Description</th>
                            <th>Status</th>
                            <th>Target Date</th>
                            
                        </tr>
                    </thead>
                    <tbody>
                        {
                            ToDos.map(
                                todo=>
                                  <tr key={todo.id}>
                                    <td>{todo.description}</td>
                                    <td>{todo.done.toString()}</td>
                                    <td>{todo.targetDate.toString()}</td>
                                    <td><button className="btn btn-danger" onClick={() => dleteToDo(todo.id)}>Delete</button></td>
                                  </tr>
                            )
                        }
                    </tbody>
                </table>
            </div>
            <button className="btn btn-success" onClick={getToDo}>get todo</button>
        </div>
    )
}
export default ToDoListComponent
