import { useEffect, useState } from 'react'
import { retrieveTodoForIdApi } from './api/TodoApiservice'
import { useAuth } from './security/AuthContext'
import { Form, useParams } from 'react-router-dom'
import { Formik } from 'formik'

function ToDoCompoent()
 {
    const {id} = useParams()
    const AuthContext = useAuth()
    const [description, setDescription] = useState('')
    const [targetDate, setTargetDate] = useState('')
    function getToDo()
    {
        retrieveTodoForIdApi(AuthContext.username,id)
        .then( 
                response => 
                    { 
                        console.log(response.data)
                        setDescription(response.data.description)
                        setTargetDate(response.data.targetDate)
                    }
             )
        .catch( error => console.log(error))

    }
    useEffect(() => {getToDo(id)}, [id])
    function onsubmit(values)
    {
        console.log(values)
    }
    return(
        <div className="container">
            <h1>To Do details</h1>
            <div>
                <Formik initialValues={ {description, targetDate} } enableReinitialize = { true } onSubmit={onsubmit}>
                {
                    (props) => 
                    {
                        <Form>
                            <fieldset className='form group m-5'>
                                <label>Description</label>
                                <input type="text" className='form-control' name = "description" />
                            </fieldset>
                            <fieldset>
                                <label>Target Date</label>
                                <input type="text" className='form-control' name = "targetDate" />
                            </fieldset>
                            <button className='btn btn-success' type='submit'> Save </button>
                        </Form>
                    }
                }
                </Formik>
            </div>
        </div>
    )
 }
 export default ToDoCompoent
