import { useEffect, useState } from "react"
import { retrieveAllToDosApi, deleteToDoApi } from "./api/TodoApiservice"
import { useAuth } from "./security/AuthContext"

function ToDoListComponent()
{
    const AuthContext = useAuth()
    console.log(AuthContext.usename)
    const [ToDos, setToDos] = useState([])
    function refreshToDos()
    {
        retrieveAllToDosApi(AuthContext.usename)
        .then( (response) => { setToDos(response.data) } )
        .catch( (error) => console.log(error))
    }
    // function getToDo()
    // {
    //     retrieveTodoForIdApi(AuthContext.usename,1)
    //     .then( response => console.log(response.data))
    //     .catch( error => console.log(error))

    // }
    const [message, setMessage] = useState(null)
    function deleteToDo(id)
    {
        deleteToDoApi(AuthContext.usename,id)
        .then(
                (response) => {
                        console.log(response)
                        setMessage(`Deleted a To Do with id ${id}`)
                        refreshToDos()
                      }
            )
        .catch( error => console.log(error))
    }

    useEffect( () => { refreshToDos()}, [] )
    return(
        <div className="container">
            <h1>Things You need to do</h1>
            <div>
                { message && <div className="alert alert-warning">{message}</div> }
                <table className="table">
                    <thead>
                        <tr>
                            <th>Description</th>
                            <th>Status</th>
                            <th>Target Date</th>
                            
                        </tr>
                    </thead>
                    <tbody>
                        {
                            ToDos.map(
                                todo=>
                                  <tr key={todo.id}>
                                    <td>{todo.description}</td>
                                    <td>{todo.done.toString()}</td>
                                    <td>{todo.targetDate.toString()}</td>
                                    <td><button className="btn btn-danger" onClick={() => deleteToDo(todo.id)}>Delete</button></td>
                                  </tr>
                            )
                        }
                    </tbody>
                </table>
            </div>
            {/* <button className="btn btn-success" onClick={getToDo}>get todo</button> */}
        </div>
    )
}
export default ToDoListComponent
