import { useState } from 'react'
import './Counter.css'
export default function Counter({by})
{

    console.log(by, typeof(by))
    let [currentCount, setCount] = useState(0)
    function increment1()
    {
        setCount(currentCount + by)
    }

    function decrement1()
    {
        if(currentCount<=1) currentCount=1
        setCount(currentCount - by)
    }
    return(<div className="counter">
                <span className="count">{currentCount}</span>
                <div>
                    <button className="decrement" onClick={decrement1}>-1</button>
                    <button className="increment" onClick={increment1}>+1</button>
                </div>
          </div>
    )

}
